    console.log(reader.result)
    reader.onloadend = () => {
        // log to console
        // logs data:<type>;base64,wL2dvYWwgbW9yZ...
        console.log(reader.result);
      };
    setImage(file)
}
    return (
        <>
          <input type="file" onChange={handleChange}/>

         { image&&
         <img src = {image}></img>}
        </>
    )
}

export default Image
